import React from "react";
import { useSelector } from "react-redux";
import "./CartDetails.css";

const CartDetails = () => {
  const { items } = useSelector((state) => state.cart);
  console.log(items);
  return (
    <>
      {items.length === 0 && <h3 className="cart_none">You Cart is empty</h3>}
      {items.length > 0 && (
        <div className="cart_container container">
          <h3 className="cart_length">
            Your Cart : {items.length} {items.length == 1 ? "Item" : "Items"}
          </h3>
          <div className="cartitem_container row d-flex justify-content-between ">
            <div className="col-12 col-lg-8">
              {items.map((item) => (
                <>
                  <hr style={{ color: "white" }}></hr>
                  <div className="cart_item " key={item._id}>
                    <div className="row container " key={item._id}>
                      <div className="col-4 col-lg-3 item_image">
                        <img
                          src={item.image}
                          alt="itemimage"
                          height="90"
                          width="115"
                        ></img>
                      </div>
                      <div className="col-5 col-lg-3 item_name d-flex  align-items-center ">
                        <p id="item_name">{item.name}</p>
                      </div>
                      <div className="col-4 col-lg-2 mt-4 mt-lg-0 d-flex  align-items-center">
                        <p id="item_price">
                          <i className="fas fa-rupee-sign"></i>
                          {item.price}
                        </p>
                      </div>
                      <div className="col-4 col-lg-3 mt-4 mt-lg-0 d-flex  align-items-center">
                        <div className="stockCounter d-flex">
                          <span className="btn btn-danger minus">-</span>

                          <input
                            type="number"
                            className="form-control count "
                            value={item.quantity}
                            readOnly
                          />

                          <span className="btn btn-primary plus">+</span>
                        </div>
                      </div>
                      <div className="col-4 col-lg-1 mt-4 mt-lg-0 d-flex justify-content-between align-items-center">
                        <i
                          id="delete_cart_item"
                          className="fa fa-trash btn btn-danger"
                        ></i>
                      </div>
                    </div>
                  </div>
                </>
              ))}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default CartDetails;

