import { createSlice } from "@reduxjs/toolkit";

const menuSlice = createSlice({
  name: "menus",
  initialState: { menus: [] },
  reducers: {
    getMenus(state, action) {
      state.menus = action.payload.menu;
    },
  },
});

export const menuActions = menuSlice.actions;

export default menuSlice;
