import axios from "axios";
import { userActions } from "./user-slice";

export const signUp = (user) => async (dispatch) => {
  try {
    dispatch(userActions.signupRequest());

    const { data } = await axios.post("/api/v1/eats/users/signup", user);
    console.log(data);
    dispatch(
      userActions.signupDetails({
        user: data.user,
      })
    );
  } catch (error) {
    dispatch(
      userActions.Errors({
        errors: error.response.data.error,
      })
    );
  }
};

export const Login = (user) => async (dispatch) => {
  try {
    dispatch(userActions.loginRequest());
    const { data } = await axios.post("/api/v1/eats/users/login", user);
    dispatch(
      userActions.loginDetails({
        user: data.user,
      })
    );
  } catch (error) {
    dispatch(
      userActions.Errors({
        errors: error.response.data.error,
      })
    );
  }
};

export const Logout = () => async (dispatch) => {
  try {
    dispatch(userActions.loginRequest());
    await axios.post("/api/v1/eats/users/logout");
    console.log("krish");
    dispatch(userActions.logOut());
  } catch (error) {
    dispatch(
      userActions.Errors({
        errors: error.response.data.error,
      })
    );
  }
};
