import React from "react";
import { useSelector } from "react-redux";

const CartDetails = () => {
  const { items } = useSelector((state) => state.cart);
  console.log(items);
  return (
    <>
      {/* Conditional rendering based on cartItems */}
      {items.length === 0 ? (
        <h2 className="mt-5">Your Cart is empty</h2>
      ) : (
        <>
          {/* Display the number  of items in the cart */}
          <h2 className="mt-5">
            Your Cart: <b>{items.length} items</b>
          </h2>

          {/* cart Items */}
          <div className="row d-flex justify-content-between cartt">
            <div className="col-12 col-lg-8">
              {items.map((item) => (
                <>
                  <hr />
                  <div className="cart-item" key={item.id}>
                    <div className="row">
                      {/* Display Item image */}
                      <div className="col-4 col-lg-3">
                        <img
                          src={item.image}
                          alt="items"
                          height="90"
                          width="115"
                        ></img>
                      </div>

                      {/* Display item Name */}
                      <div className="col-5 col-lg-3"> {item.name}</div>

                      {/* Display item price */}
                      <div className="col-4 col-lg-2 mt-4 mt-lg-0">
                        <p id="card_item_price">
                          {/* <FontAwesomeIcon icon={faIndianRupee} size="xs" /> */}
                          {item.price}
                        </p>
                      </div>

                      {/* Quantity control */}
                      <div className="col-4 col-lg-3 mt-4 mt-lg-0">
                        <div className="stockCounter d-inline">
                          {/* Decrease qty button */}
                          <span
                            className="btn btn-danger minus"
                            // onClick={() =>
                            //   decreaseQty(item.fooditem, item.quantity)
                            // }
                          >
                            -
                          </span>
                          {/* Display current quantity */}
                          <input
                            type="number"
                            className="form-control count d-inline"
                            value={item.quantity}
                            readOnly
                          />
                          {/* Increase quantity button */}
                          <span
                            className="btn btn-primary plus"
                            // onClick={() =>
                            //   increaseQty(
                            //     item.fooditem,
                            //     item.quantity,
                            //     item.stock
                            //   )
                            // }
                          >
                            +
                          </span>
                        </div>
                      </div>
                      {/* Remove item buttom */}
                      <div className="col-4 col-lg-1 mt-4 mt-lg-0">
                        <i
                          id="delete_cart_item"
                          className="fa fa-trash btn btn-danger"
                          //   onClick={() => removeCartItemHandler(item.fooditem)}
                        ></i>
                      </div>
                    </div>
                  </div>
                </>
              ))}
            </div>
          </div>
        </>
      )}
    </>
  );
};

export default CartDetails;
