import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../Header.css";
import { useSelector } from "react-redux";
//Cart Component
function Cart() {
  const { totalQuantity, isShaking } = useSelector((state) => state.cart);

  const [Shaking, setShaking] = useState(isShaking);
  console.log(Shaking);

  useEffect(() => {
    if (Shaking) {
      // // Trigger the animation by adding the CSS class
      // const element = document.getElementById("elementToShake");
      // element.classList.add("shake-animation");

      // Remove the class and stop the animation after 2 seconds
      const animationTimer = setTimeout(() => {
        setShaking(false); // Reset the state to stop the animation
      }, 3000);

      return () => {
        // Clear the timer if the component unmounts or state changes
        clearTimeout(animationTimer);
      };
    }
  }, [Shaking]);

  return (
    <>
      <Link
        to=".."
        className="nav-link cart  active"
        style={{ textDecoration: "none", color: "white" }}
      >
        <span className="cartname">Cart</span>
        <span
          id=""
          className={`cartnumber ${isShaking ? "shake-animation" : " "}`}
          style={{ display: "inline-block" }}
        >
          {totalQuantity}
        </span>
      </Link>
    </>
  );
}

export default Cart;
